import React from 'react';

export class Item extends React.Component {
    // const { product } = props;

    render () {
        return (
            <li className="product row">
                <div className="col-product">
                <figure className="product-image">
                    <img src={require(`../../img/${product.img}.png`)} alt="Cap" />
                    <div className="product-description">
                    <h1>{product.name}</h1>
                    <p className="product-code">Product code {product.sku}</p>
                    </div>
                </figure>
                </div>
                <div className="col-quantity">
                <button className="count" onClick={()=> {}}>-</button>
                <input type="text" className="product-quantity" onChange={onInputChange} value={product.qty} />
                <button className="count" onClick={()=> {}}>+</button>
                </div>
                <div className="col-price">
                <span className="product-price">{product.price}</span
                ><span className="product-currency currency">€</span>
                </div>
                <div className="col-total">
                <span className="product-price">{product.qty * product.price}</span>
                <span className="product-currency currency">€</span>
                </div>
            </li>
        );
    };
}
