import React from 'react';
import { Item } from '../item/item';
import './cart-container.css';


export class CartContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            cart: []
        }
    }

    onChange = (product) => {
        const productsInCart = this.state.cart;
        const indexProduct = this.state.cart.findIndex(addedProduct => addedProduct.sku === product.sku)

        if (indexProduct < 0) {
            productsInCart.push(product)
        } else {
            productsInCart[indexProduct] = product;
        }
        this.setState({
            cart: productsInCart
        }, () => { console.log(this.state.cart)})
    }

    render() {
        const products = this.props.products;

        return (
            <div className="cart-container">
                <section className="products">
                    <h1 className="main">Shopping cart</h1>
                    <ul className="products-list tableHead">
                        <li className="products-list-title row">
                            <div className="col-product">Product details</div>
                            <div className="col-quantity">Quantity</div>
                            <div className="col-price">Price</div>
                            <div className="col-total">Total</div>
                        </li>
                    </ul>
                    <ul className="products-list">
                        {products.map(product => <Item product={product} onQuantityChanged={this.onChange} />)}
                    </ul>
                </section>
                <aside className="summary">
                    <h1 className="main">Order Summary</h1>
                    <ul className="summary-items wrapper border">
                        <li>
                            <span className="summary-items-number">11 Items</span
                            ><span className="summary-items-price"
                            >120<span className="currency">€</span></span
                            >
                        </li>
                    </ul>
                    <div className="summary-discounts wrapper-half border">
                        <h2>Discounts</h2>
                        <ul>
                            <li><span>2x1 Mug offer</span><span>-10€</span></li>
                            <li><span>x3 Shirt offer</span><span>-3€</span></li>
                            <li><span>Promo code</span><span>0€</span></li>
                        </ul>
                    </div>
                    <div className="summary-total wrapper">
                        <ul>
                            <li>
                                <span className="summary-total-cost">Total cost</span
                                ><span className="summary-total-price">107€</span>
                            </li>
                        </ul>
                        <button type="submit">Checkout</button>
                    </div>
                </aside>
            </div>
        );
    }
}
